    <script>
        document.addEventListener('DOMContentLoaded', function () {
            const dataElement = document.getElementById('analysis-data');
            const analysisData = JSON.parse(dataElement.textContent);

            function formatKey(key) {
                return key
                    .replace(/_/g, ' ')
                    .replace(/\b\w/g, l => l.toUpperCase());
            }

            function renderList(containerId, items) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    return;
                }

                const list = document.createElement('ul');
                items.forEach(item => {
                    const li = document.createElement('li');
                    li.textContent = item;
                    list.appendChild(li);
                });
                container.innerHTML = '';
                container.appendChild(list);
            }

            function renderTextContent(containerId, text) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!text || text.trim() === '') {
                    container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    return;
                }

                container.innerHTML = '<div class="action-text-content"></div>';
                const contentDiv = container.querySelector('.action-text-content');

                const paragraphs = text.split('\n\n');
                paragraphs.forEach(paragraph => {
                    if (paragraph.trim()) {
                        const p = document.createElement('p');
                        p.textContent = paragraph.trim();
                        contentDiv.appendChild(p);
                    }
                });
            }


function renderActions(containerId, actions) {
    const container = document.getElementById(containerId);
    if (!container) return;

    if (!actions || typeof actions !== 'object') {
        container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
        return;
    }

    container.innerHTML = '';

    // Erwartet: handlungsempfehlungen = { key1: "Text...", key2: "Text..." }
    for (const [key, value] of Object.entries(actions)) {
        const wrapper = document.createElement('div');
        wrapper.classList.add('action-text-content');

        const title = document.createElement('h3');
        title.textContent = formatKey(key);   // <-- JSON-Attributname als Titel
        wrapper.appendChild(title);

        if (typeof value === 'string' && value.trim() !== '') {
            const p = document.createElement('p');
            p.textContent = value.trim();     // <-- Inhalt = Wert
            wrapper.appendChild(p);
        }

        container.appendChild(wrapper);
    }

    if (!container.innerHTML.trim()) {
        container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
    }
}


            function renderSection(containerId, sectionData) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (Array.isArray(sectionData)) {
                    renderList(containerId, sectionData);
                } else if (typeof sectionData === 'object' && sectionData !== null) {
                    container.innerHTML = '';
                    for (const [key, value] of Object.entries(sectionData)) {
                        if (Array.isArray(value) && value.length > 0) {
                            const title = document.createElement('h3');
                            title.textContent = formatKey(key);
                            container.appendChild(title);

                            const list = document.createElement('ul');
                            value.forEach(item => {
                                const li = document.createElement('li');
                                li.textContent = item;
                                list.appendChild(li);
                            });
                            container.appendChild(list);
                        }
                    }

                    if (container.innerHTML === '') {
                        container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    }
                } else if (typeof sectionData === 'string') {
                    renderTextContent(containerId, sectionData);
                }
            }

            renderSection('erster-eindruck', analysisData.erster_eindruck);
            renderSection('schwaechen-risiken', analysisData.schwaechen_und_risiken);
            renderSection('staerken-genutzt', analysisData.chancen_und_potenziale?.staerken_die_genutzt_werden_koennen);
            renderSection('kommunikationsansaetze', analysisData.chancen_und_potenziale?.kommunikationsansaetze);

            // hier werden die Handlungsempfehlungen gerendert
            renderActions('handlungsempfehlungen', analysisData.handlungsempfehlungen);
        });
    </script>

</body>

</html>

