    <script id="publication-data" type="application/json">
        {{ JSON.stringify($json.Inhalt) }}
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dataElement = document.getElementById('publication-data');
            const publicationData = JSON.parse(dataElement.textContent);
            const data = publicationData.veroeffentlichung_stellenanzeige;

            function renderList(containerId, items) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    return;
                }

                const list = document.createElement('ul');
                items.forEach(item => {
                    const li = document.createElement('li');
                    li.innerHTML = `<span class="data-value">${item}</span>`;
                    list.appendChild(li);
                });
                container.innerHTML = '';
                container.appendChild(list);
            }

            function renderTextValue(containerId, text) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!text || text.trim() === '') {
                    container.textContent = 'Keine Angabe';
                    container.classList.add('empty');
                    return;
                }

                container.textContent = text;
                container.classList.remove('empty');
            }

            function renderTextContent(containerId, text) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!text || text.trim() === '') {
                    container.textContent = 'Keine Begründung verfügbar';
                    container.classList.add('empty');
                    return;
                }

                container.textContent = text;
                container.classList.remove('empty');
            }

            // Render all data
            renderList('webseiten-plattformen', data.webseiten_und_plattformen);
            renderTextValue('anzahl', data.anzahl);
            renderTextValue('kosten-schaetzung-zahl', data.kosten_schaetzung_zahl);
            renderTextContent('kosten-schaetzung-begruendung', data.kosten_schaetzung_begründung);
            renderTextValue('anzeigen-laufzeit-schaetzung', data.anzeigen_laufzeit_schaetzung);
        });
    </script>
</body>
</html>

