<body>
    <div class="container">
        <header>
            <h1>Externe Bewertungen Analyse</h1>
            <div class="subtitle">Aggregierte Auswertung aus verschiedenen Bewertungsplattformen</div>
        </header>

        <main>
            <div class="overview-section">
                <div class="card card-rating">
                    <div class="card-header">
                        <div class="icon icon-rating">
                            <span class="material-icons">star</span>
                        </div>
                        <h2>Gesamtwertung</h2>
                    </div>
                    <div id="gesamt-wertung" class="rating-display">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card">
                    <div class="card-header">
                        <div class="icon">
                            <span class="material-icons">source</span>
                        </div>
                        <h2>Quellen</h2>
                    </div>
                    <div id="quellen-container" class="sources-grid">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>
            </div>

            <div class="section-divider"></div>

            <h3>Detaillierte Analyse</h3>

            <div class="analysis-grid">
                <div class="card card-strengths">
                    <div class="card-header">
                        <div class="icon icon-strengths">
                            <span class="material-icons">add_circle</span>
                        </div>
                        <h2>Stärken</h2>
                    </div>
                    <div id="staerken" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-weaknesses">
                    <div class="card-header">
                        <div class="icon icon-weaknesses">
                            <span class="material-icons">remove_circle</span>
                        </div>
                        <h2>Schwächen</h2>
                    </div>
                    <div id="schwaechen" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-impression">
                    <div class="card-header">
                        <div class="icon icon-impression">
                            <span class="material-icons">description</span>
                        </div>
                        <h2>Gesamteindruck</h2>
                    </div>
                    <div id="gesamt-eindruck" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>
            </div>
        </main>
    </div>

    <script id="reviews-data" type="application/json">
            {{ JSON.stringify($json.Inhalt) }}
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dataElement = document.getElementById('reviews-data');
            const reviewsData = JSON.parse(dataElement.textContent);
            const data = reviewsData.externe_bewertungen;

            function renderRating() {
                const container = document.getElementById('gesamt-wertung');
                if (!container) return;

                if (!data.gesamt_wertung || data.gesamt_wertung.trim() === '') {
                    container.innerHTML = `
                        <div class="rating-value">N/A</div>
                        <div class="rating-label">Keine Bewertung verfügbar</div>
                    `;
                    return;
                }

                container.innerHTML = `
                    <div class="rating-value">N/A</div>
                    <div class="rating-label">${data.gesamt_wertung}</div>
                `;
            }

            function renderSources() {
                const container = document.getElementById('quellen-container');
                if (!container) return;

                if (!data.quellen || data.quellen.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Quellen verfügbar</div>';
                    return;
                }

                container.innerHTML = '';
                data.quellen.forEach(source => {
                    const sourceCard = document.createElement('div');
                    sourceCard.className = 'source-card';

                    let ratingDisplay = '';
                    if (source.rating && source.rating !== 'Nicht verfügbar') {
                        ratingDisplay = `<div class="data-item">${source.rating}/5</div>`;
                    }

                    let reviewCountDisplay = '';
                    if (source.anzahl_bewertungen && source.anzahl_bewertungen !== 'Nicht verfügbar') {
                        reviewCountDisplay = `<div class="meta-item">${source.anzahl_bewertungen} Bewertungen</div>`;
                    }

                    let feedbackDisplay = '';
                    if (source.haupt_feedback && source.haupt_feedback !== 'Keine Bewertungstexte zur Analyse bereitgestellt. Es wurde lediglich ein Link zur Plattform übergeben.') {
                        feedbackDisplay = `
                            <div class="source-meta">
                                <strong>Hauptfeedback:</strong> ${source.haupt_feedback}
                            </div>
                        `;
                    }

                    let portalLink = '';
                    if (source.portal && source.portal.toLowerCase() === 'kununu') {
                        portalLink = `<a href="https://www.kununu.com" target="_blank" rel="noopener noreferrer" class="source-link">Zu Kununu</a>`;
                    }

                    sourceCard.innerHTML = `
                        <div class="source-header">
                            <div class="source-portal">${source.portal || 'Unbekannte Quelle'}</div>
                            ${ratingDisplay}
                        </div>
                        ${reviewCountDisplay}
                        ${feedbackDisplay}
                        ${portalLink}
                    `;

                    container.appendChild(sourceCard);
                });
            }

            function renderList(containerId, items) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    return;
                }

                const list = document.createElement('ul');
                items.forEach(item => {
                    const li = document.createElement('li');
                    li.innerHTML = `<span class="data-item">${item}</span>`;
                    list.appendChild(li);
                });
                container.innerHTML = '';
                container.appendChild(list);
            }

            renderRating();
            renderSources();
            renderList('staerken', data.analyse?.staerken || []);
            renderList('schwaechen', data.analyse?.schwaechen || []);
            renderList('gesamt-eindruck', data.analyse?.gesamt_eindruck || []);
        });
    </script>
</body>
</html>

