    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dataElement = document.getElementById('vacancy-data');
            const vacancyData = JSON.parse(dataElement.textContent);

            function renderDuration() {
                const container = document.getElementById('besetzungsdauer-content');
                if (!container) return;

                const data = vacancyData.besetzungsdauer;

                let html = '<div class="metric-grid">';

                if (data.tage_min) {
                    html += `
                        <div class="metric-card">
                            <div class="metric-value">${data.tage_min}</div>
                            <div class="metric-label">Min. Tage</div>
                        </div>
                    `;
                }

                if (data.tage_avg) {
                    html += `
                        <div class="metric-card">
                            <div class="metric-value">${data.tage_avg}</div>
                            <div class="metric-label">Ø Tage</div>
                        </div>
                    `;
                }

                if (data.tage_max) {
                    html += `
                        <div class="metric-card">
                            <div class="metric-value">${data.tage_max}</div>
                            <div class="metric-label">Max. Tage</div>
                        </div>
                    `;
                }

                html += '</div>';

                if (data.begruendung) {
                    html += `<div class="text-content">${data.begruendung}</div>`;
                }

                container.innerHTML = html || '<div class="empty-state">Keine Daten verfügbar</div>';
            }

            function renderCostOfVacancy() {
                const container = document.getElementById('cost-of-vacancy-content');
                if (!container) return;

                const data = vacancyData.cost_of_vacancy;

                let html = '';

                if (data.betrag_eur) {
                    html += `<div class="cost-amount">${data.betrag_eur}</div>`;
                }

                if (data.formel) {
                    html += `<div class="formula-display">${data.formel}</div>`;
                }

                if (data.annahmen) {
                    const annahmen = data.annahmen;
                    if (annahmen.jahresgehalt_eur || annahmen.arbeitstage_pro_jahr || annahmen.stellenwert_faktor || annahmen.time_to_fill_tage) {
                        html += '<div class="assumption-grid">';

                        if (annahmen.jahresgehalt_eur) {
                            html += `
                                <div class="assumption-item">
                                    <div class="assumption-label">Jahresgehalt</div>
                                    <div class="assumption-value">${annahmen.jahresgehalt_eur}</div>
                                </div>
                            `;
                        }

                        if (annahmen.arbeitstage_pro_jahr) {
                            html += `
                                <div class="assumption-item">
                                    <div class="assumption-label">Arbeitstage/Jahr</div>
                                    <div class="assumption-value">${annahmen.arbeitstage_pro_jahr}</div>
                                </div>
                            `;
                        }

                        if (annahmen.stellenwert_faktor) {
                            html += `
                                <div class="assumption-item">
                                    <div class="assumption-label">Stellenwert-Faktor</div>
                                    <div class="assumption-value">${annahmen.stellenwert_faktor}</div>
                                </div>
                            `;
                        }

                        if (annahmen.time_to_fill_tage) {
                            html += `
                                <div class="assumption-item">
                                    <div class="assumption-label">Time to Fill (Tage)</div>
                                    <div class="assumption-value">${annahmen.time_to_fill_tage}</div>
                                </div>
                            `;
                        }

                        html += '</div>';
                    }
                }

                container.innerHTML = html || '<div class="empty-state">Keine Daten verfügbar</div>';
            }

            function renderRisk() {
                const container = document.getElementById('risiko-vakanz-content');
                if (!container) return;

                const data = vacancyData.risiko_vakanz;

                let html = '';

                if (data.einschaetzung) {
                    html += `<div class="text-content" style="font-size: 1.5rem; font-weight: 600; text-align: center; margin: 20px 0;">${data.einschaetzung}</div>`;
                }

                if (data.beschreibung) {
                    html += `<div class="text-content">${data.beschreibung}</div>`;
                }

                container.innerHTML = html || '<div class="empty-state">Keine Daten verfügbar</div>';
            }

            function renderBudget() {
                const container = document.getElementById('empfohlenes-budget-content');
                if (!container) return;

                const data = vacancyData.empfohlenes_budget;

                let html = '';

                if (data.budget_min_eur && data.budget_max_eur) {
                    html += `
                        <div class="budget-range">
                            <div class="budget-min">
                                <div class="budget-value">${data.budget_min_eur}</div>
                                <div class="budget-label">Minimum</div>
                            </div>
                            <div class="budget-separator">—</div>
                            <div class="budget-max">
                                <div class="budget-value">${data.budget_max_eur}</div>
                                <div class="budget-label">Maximum</div>
                            </div>
                        </div>
                    `;
                } else if (data.budget_min_eur) {
                    html += `<div class="cost-amount" style="color: #8e2de2;">${data.budget_min_eur}</div>`;
                } else if (data.budget_max_eur) {
                    html += `<div class="cost-amount" style="color: #8e2de2;">${data.budget_max_eur}</div>`;
                }

                if (data.beschreibung) {
                    html += `<div class="text-content">${data.beschreibung}</div>`;
                }

                container.innerHTML = html || '<div class="empty-state">Keine Daten verfügbar</div>';
            }

            renderDuration();
            renderCostOfVacancy();
            renderRisk();
            renderBudget();
        });
    </script>
</body>
</html>

