    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dataElement = document.getElementById('market-data');
            const marketData = JSON.parse(dataElement.textContent);
            const data = marketData.marktpositionierung;

            function renderList(containerId, items, isNumberList = false) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Daten verfügbar</div>';
                    return;
                }

                if (isNumberList && items.every(item => !isNaN(parseFloat(item)))) {
                    const sum = items.reduce((total, item) => total + parseFloat(item), 0);
                    const avg = sum / items.length;

                    container.innerHTML = `
                        <div class="metric-display">
                            <div class="metric-value">${avg.toFixed(1)}</div>
                            <div class="metric-label">Ø Tage</div>
                        </div>
                        <div style="margin-top: 20px;">
                            <div class="section-title">Einzelwerte</div>
                            ${items.map(item => `<div class="data-item">${item} Tage</div>`).join('')}
                        </div>
                    `;
                } else {
                    const list = document.createElement('ul');
                    items.forEach(item => {
                        const li = document.createElement('li');
                        li.textContent = item;
                        list.appendChild(li);
                    });
                    container.innerHTML = '';
                    container.appendChild(list);
                }
            }

            function renderCountList(containerId, items) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Daten verfügbar</div>';
                    return;
                }

                container.innerHTML = `
                    <div class="metric-display">
                        <div class="metric-value">${items.length}</div>
                        <div class="metric-label">Stellen gefunden</div>
                    </div>
                    <div style="margin-top: 20px;">
                        <div class="section-title">Details</div>
                        ${items.map(item => `<div class="data-item">${item}</div>`).join('')}
                    </div>
                `;
            }

            function renderOverall() {
                const container = document.getElementById('gesamtbewertung');
                if (!container) return;

                if (!data.gesamtbewertung_positionierung || data.gesamtbewertung_positionierung.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Gesamtbewertung verfügbar</div>';
                    return;
                }

                container.innerHTML = data.gesamtbewertung_positionierung
                    .map(item => `<p>${item}</p>`)
                    .join('');
            }

            renderCountList('aehnliche-stellen', data.anzahl_aehnlicher_stellen_im_umkreis);
            renderList('schaltungsdauer', data.durchschnittliche_schaltungsdauer, true);
            renderList('gehaltsangaben', data.vergleich_konkurrenzbedingungen?.gehaltsangaben || []);
            renderList('arbeitsmodelle', data.vergleich_konkurrenzbedingungen?.arbeitsmodelle || []);
            renderList('benefits', data.vergleich_konkurrenzbedingungen?.benefits || []);
            renderOverall();
        });
    </script>
