<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Maßnahmen & Budgetplanung</title>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap" rel="stylesheet">
    <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons">
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Inter', sans-serif;
            line-height: 1.6;
            color: #ffffff;
            background: linear-gradient(135deg, #4A001F 0%, #001733 50%, #000A1A 100%);            min-height: 100vh;
            padding: 60px 40px;
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
        }

        header {
            text-align: center;
            margin-bottom: 60px;
            padding-bottom: 30px;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
        }

        h1 {
            font-size: 3rem;
            font-weight: 800;
            color: #ffffff;
            margin-bottom: 12px;
            letter-spacing: -0.5px;
            background: linear-gradient(90deg, #FFD700, #FFA500);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
        }

        .subtitle {
            font-size: 1.25rem;
            color: rgba(255, 255, 255, 0.9);
            font-weight: 400;
            letter-spacing: 0.5px;
        }

        .measures-grid {
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            gap: 30px;
            margin-bottom: 50px;
        }

        .budget-grid {
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            gap: 30px;
        }

        .card {
            background: linear-gradient(145deg, rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0.05));
            border-radius: 20px;
            padding: 35px;
            border: 1px solid rgba(255, 255, 255, 0.1);
            backdrop-filter: blur(10px);
            box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
            height: 100%;
            position: relative;
            overflow: hidden;
        }

        .card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 6px;
            height: 100%;
        }

        .card-short::before {
            background: linear-gradient(180deg, #FF416C 0%, #FF4B2B 100%);
        }

        .card-medium::before {
            background: linear-gradient(180deg, #00B4DB 0%, #0083B0 100%);
        }

        .card-long::before {
            background: linear-gradient(180deg, #11998e 0%, #38ef7d 100%);
        }

        .card-low::before {
            background: linear-gradient(180deg, #FF416C 0%, #FF4B2B 100%);
        }

        .card-recommended::before {
            background: linear-gradient(180deg, #00B09B 0%, #96C93D 100%);
        }

        .card-high::before {
            background: linear-gradient(180deg, #8E2DE2 0%, #4A00E0 100%);
        }

        .card-header {
            display: flex;
            align-items: center;
            margin-bottom: 25px;
        }

        .icon {
            font-size: 28px;
            margin-right: 20px;
            width: 60px;
            height: 60px;
            border-radius: 12px;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .icon-short {
            background: linear-gradient(145deg, rgba(255, 65, 108, 0.3), rgba(255, 75, 43, 0.3));
        }

        .icon-medium {
            background: linear-gradient(145deg, rgba(0, 180, 219, 0.3), rgba(0, 131, 176, 0.3));
        }

        .icon-long {
            background: linear-gradient(145deg, rgba(17, 153, 142, 0.3), rgba(56, 239, 125, 0.3));
        }

        .icon-low {
            background: linear-gradient(145deg, rgba(255, 65, 108, 0.3), rgba(255, 75, 43, 0.3));
        }

        .icon-recommended {
            background: linear-gradient(145deg, rgba(0, 176, 155, 0.3), rgba(150, 201, 61, 0.3));
        }

        .icon-high {
            background: linear-gradient(145deg, rgba(142, 45, 226, 0.3), rgba(74, 0, 224, 0.3));
        }

        h2 {
            font-size: 1.75rem;
            font-weight: 700;
            color: #ffffff;
            letter-spacing: -0.3px;
        }

        .content-section {
            margin-top: 10px;
        }

        ul {
            padding-left: 20px;
        }

        li {
            margin-bottom: 16px;
            color: rgba(255, 255, 255, 0.9);
            font-size: 1.125rem;
            line-height: 1.7;
            padding-left: 10px;
        }

        .timeline-badge {
            display: inline-block;
            padding: 4px 12px;
            border-radius: 20px;
            font-size: 0.75rem;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 1px;
            margin-bottom: 15px;
        }

        .badge-short {
            background: rgba(255, 65, 108, 0.2);
            color: #FF416C;
        }

        .badge-medium {
            background: rgba(0, 180, 219, 0.2);
            color: #00B4DB;
        }

        .badge-long {
            background: rgba(17, 153, 142, 0.2);
            color: #11998e;
        }

        .badge-low {
            background: rgba(255, 65, 108, 0.2);
            color: #FF416C;
        }

        .badge-recommended {
            background: rgba(0, 176, 155, 0.2);
            color: #00B09B;
        }

        .badge-high {
            background: rgba(142, 45, 226, 0.2);
            color: #8E2DE2;
        }

        .budget-amount {
            font-size: 2.5rem;
            font-weight: 800;
            text-align: center;
            margin: 20px 0;
        }

        .amount-low {
            color: #FF416C;
            text-shadow: 0 0 20px rgba(255, 65, 108, 0.3);
        }

        .amount-recommended {
            color: #00B09B;
            text-shadow: 0 0 20px rgba(0, 176, 155, 0.3);
        }

        .amount-high {
            color: #8E2DE2;
            text-shadow: 0 0 20px rgba(142, 45, 226, 0.3);
        }

        .empty-state {
            color: rgba(255, 255, 255, 0.5);
            font-style: italic;
            font-size: 1.125rem;
            padding: 20px 0;
            text-align: center;
        }

        .section-title {
            font-size: 0.875rem;
            text-transform: uppercase;
            letter-spacing: 2px;
            color: #FFD700;
            margin: 50px 0 20px 0;
            font-weight: 600;
            text-align: center;
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
            <h1>Maßnahmen & Budgetplanung</h1>
            <div class="subtitle">Zeitliche Umsetzung und finanzielle Planung</div>
        </header>

        <main>
            <div class="section-title">Zeitliche Maßnahmenplanung</div>
            <div class="measures-grid">
                <div class="card card-short">
                    <div class="timeline-badge badge-short">Innerhalb 1-3 Monaten</div>
                    <div class="card-header">
                        <div class="icon icon-short">
                            <span class="material-icons">flash_on</span>
                        </div>
                        <h2>Kurzfristig</h2>
                    </div>
                    <div id="kurzfristig" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-medium">
                    <div class="timeline-badge badge-medium">Innerhalb 3-12 Monaten</div>
                    <div class="card-header">
                        <div class="icon icon-medium">
                            <span class="material-icons">trending_up</span>
                        </div>
                        <h2>Mittelfristig</h2>
                    </div>
                    <div id="mittelfristig" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-long">
                    <div class="timeline-badge badge-long">Ab 12+ Monaten</div>
                    <div class="card-header">
                        <div class="icon icon-long">
                            <span class="material-icons">timeline</span>
                        </div>
                        <h2>Langfristig</h2>
                    </div>
                    <div id="langfristig" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>
            </div>

            <div class="section-title">Budget Szenarien</div>
            <div class="budget-grid">
                <div class="card card-low">
                    <div class="timeline-badge badge-low">Minimalvariante</div>
                    <div class="card-header">
                        <div class="icon icon-low">
                            <span class="material-icons">savings</span>
                        </div>
                        <h2>Low Budget</h2>
                    </div>
                    <div id="budget-low" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-recommended">
                    <div class="timeline-badge badge-recommended">Empfohlen</div>
                    <div class="card-header">
                        <div class="icon icon-recommended">
                            <span class="material-icons">recommend</span>
                        </div>
                        <h2>Recommended</h2>
                    </div>
                    <div id="budget-recommended" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>

                <div class="card card-high">
                    <div class="timeline-badge badge-high">Premiumvariante</div>
                    <div class="card-header">
                        <div class="icon icon-high">
                            <span class="material-icons">workspace_premium</span>
                        </div>
                        <h2>High Budget</h2>
                    </div>
                    <div id="budget-high" class="content-section">
                        <!-- Content will be populated by JavaScript -->
                    </div>
                </div>
            </div>
        </main>
    </div>

    <script id="measures-data" type="application/json">
{{ JSON.stringify($json.Inhalt) }}
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dataElement = document.getElementById('measures-data');
            const measuresData = JSON.parse(dataElement.textContent);

            function renderList(containerId, items, isBudget = false) {
                const container = document.getElementById(containerId);
                if (!container) return;

                if (!items || items.length === 0) {
                    container.innerHTML = '<div class="empty-state">Keine Einträge vorhanden</div>';
                    return;
                }

                if (isBudget && items.length === 1 && !isNaN(parseFloat(items[0]))) {
                    const amount = items[0];
                    const amountClass = containerId.includes('low') ? 'amount-low' : 
                                      containerId.includes('recommended') ? 'amount-recommended' : 'amount-high';

                    container.innerHTML = `
                        <div class="budget-amount ${amountClass}">${amount}</div>
                    `;
                } else if (isBudget && items.length === 1) {
                    container.innerHTML = `
                        <div class="budget-amount">${items[0]}</div>
                    `;
                } else {
                    const list = document.createElement('ul');
                    items.forEach(item => {
                        const li = document.createElement('li');
                        li.textContent = item;
                        list.appendChild(li);
                    });
                    container.innerHTML = '';
                    container.appendChild(list);
                }
            }

            renderList('kurzfristig', measuresData.massnahmen?.kurzfristig || []);
            renderList('mittelfristig', measuresData.massnahmen?.mittelfristig || []);
            renderList('langfristig', measuresData.massnahmen?.langfristig || []);
            renderList('budget-low', measuresData.budget?.low || [], true);
            renderList('budget-recommended', measuresData.budget?.recommended || [], true);
            renderList('budget-high', measuresData.budget?.high || [], true);
        });
    </script>
</body>
</html>
